Source code # :::spoiler Source Code
#!/usr/bin/env python from Crypto.Cipher import AES from key import KEY import os import math BLOCK_SIZE = 16 UMAX = int(math.pow(256, BLOCK_SIZE)) def to_bytes(n): s = hex(n) s_n = s[2:] if &#39;L&#39; in s_n: s_n = s_n.replace(&#39;L&#39;, &#39;&#39;) if len(s_n) % 2 != 0: s_n = &#39;0&#39; + s_n decoded = s_n.decode(&#39;hex&#39;) pad = (len(decoded) % BLOCK_SIZE) if pad != 0: decoded = &#34;\0&#34; * (BLOCK_SIZE - pad) + decoded return decoded def remove_line(s): # returns the header line, and the rest of the file return s[:s.index(&#39;\n&#39;) + 1], s[s.index(&#39;\n&#39;)+1:] def parse_header_ppm(f): data = f.read() header = &#34;&#34; for i in range(3): header_i, data = remove_line(data) header += header_i return header, data def pad(pt): padding = BLOCK_SIZE - len(pt) % BLOCK_SIZE return pt + (chr(padding) * padding) def aes_abc_encrypt(pt): cipher = AES.new(KEY, AES.MODE_ECB) ct = cipher.encrypt(pad(pt)) blocks = [ct[i * BLOCK_SIZE:(i+1) * BLOCK_SIZE] for i in range(len(ct) / BLOCK_SIZE)] iv = os.urandom(16) blocks.insert(0, iv) for i in range(len(blocks) - 1): prev_blk = int(blocks[i].encode(&#39;hex&#39;), 16) curr_blk = int(blocks[i+1].encode(&#39;hex&#39;), 16) n_curr_blk = (prev_blk + curr_blk) % UMAX blocks[i+1] = to_bytes(n_curr_blk) ct_abc = &#34;&#34;.join(blocks) return iv, ct_abc, ct if __name__==&#34;__main__&#34;: with open(&#39;flag.ppm&#39;, &#39;rb&#39;) as f: header, data = parse_header_ppm(f) iv, c_img, ct = aes_abc_encrypt(data) with open(&#39;body.enc.ppm&#39;, &#39;wb&#39;) as fw: fw.write(header) fw.write(c_img) :::
Recon # 這一題也蠻有趣的，可以先看一下他怎麼加密的
先把ppm file的header, data parse出來 在51行用AES-ECB加密data，而我們知道ECB mode就很不安全 在53行再把每一個block分出來並在開頭的地方插入initial vector 57-62行的for-loop，就是把兩個block相加再mod UMAX就是對應的下一個block的值，意即: $$ c[0] \leftarrow Initial\ Vector=AES[0]\ c[i+1] \leftarrow (AES[i+1]+c[i])\ % \ 2^{128}\ k2^{128} \leftarrow AES[i+1]+c[i]-c[i+1],\ k \in {0,1}, {AES[\ ], c[\ ]} \in 2^{128} $$ 所以綜上所述，我們可以把ciphertext還原成AES的版本，這樣應該可以看到flag的一些資訊，即使不知道一開始的key也可以(從最後面的block算回來) $$ AES[i] \leftarrow k2^{128}-c[i-1]+c[i] $$ Exploit # #!/usr/bin/env python from Crypto.Cipher import AES # from key import KEY import os import math BLOCK_SIZE = 16 UMAX = int(math.pow(256, BLOCK_SIZE)) def to_bytes(n): s = hex(n) s_n = s[2:] if &#39;L&#39; in s_n: s_n = s_n.replace(&#39;L&#39;, &#39;&#39;) if len(s_n) % 2 != 0: s_n = &#39;0&#39; + s_n decoded = bytes.fromhex(s_n)#s_n.decode(&#39;hex&#39;) pad = (len(decoded) % BLOCK_SIZE) if pad != 0: decoded = b&#34;\0&#34; * (BLOCK_SIZE - pad) + decoded return decoded def remove_line(s): # returns the header line, and the rest of the file return s[:s.index(b&#39;\n&#39;) + 1], s[s.index(b&#39;\n&#39;)+1:] def parse_header_ppm(f): data = f.read() header = b&#34;&#34; for i in range(3): header_i, data = remove_line(data) header += header_i return header, data def pad(pt): padding = BLOCK_SIZE - len(pt) % BLOCK_SIZE return pt + (chr(padding) * padding) def abc_decrypt(ct): blocks = [ct[i * BLOCK_SIZE:(i+1) * BLOCK_SIZE] for i in range(len(ct) // BLOCK_SIZE)] k = 0 for idx in range(len(blocks)-1, 0, -1): curr_blk = int(blocks[idx].hex(), 16) prev_blk = int(blocks[idx-1].hex(), 16) if (k * UMAX + curr_blk - prev_blk) &lt; 0: tmp = UMAX + curr_blk - prev_blk else: tmp = curr_blk - prev_blk blocks[idx] = to_bytes(tmp) pt_abc = b&#34;&#34;.join(blocks) return pt_abc if __name__==&#34;__main__&#34;: with open(&#39;body.enc.ppm&#39;, &#39;rb&#39;) as f: header, data = parse_header_ppm(f) pt_img = abc_decrypt(data) # iv, c_img, ct = aes_abc_encrypt(data) with open(&#39;body.dec.ppm&#39;, &#39;wb&#39;) as fw: fw.write(header) fw.write(pt_img) Flag: picoCTF{d0Nt_r0ll_yoUr_0wN_aES}
